import React, { Component } from 'react'
import './index.css'

export default class Parent extends Component {

    state={carName:'奔驰C63'}


    changeCar=()=>{
        this.setState({carName:'迈巴赫'})
    }

  render() {
    return (
      <div className='parent'>
        <h3>我是Parent组件</h3>
        <button onClick={this.changeCar}>点我换车</button>
        <Child />
      </div>
    )
  }
}

class Child extends Component {
    render() {
      return (
        <div className='child'>
            <h3>我是Child组件</h3>
        </div>
      )
    }
}
