//引入Count的UI组件
import CountUI from '../../components/Count'
//引入actionCreator
import {createIncrementAction} from '../../redux/count_action_creator'
//引入connect用于连接UI组件与redux
import {connect} from 'react-redux'


//a函数返回的对象中的key就作为传递给UI组件props的key，value就作为传递给UI组件props的value————状态
function mapStateToProps(state){
    return {count:state}
}

//b函数返回的对象中的key就作为传递给UI组件props的key，value就作为传递给UI组件props的value————操作状态的方法
function map(dispatch){
    //通知redux执行加法
    return {jia:(number)=>{
        dispatch(createIncrementAction(number))
    }}
}

//使用connetct()()创建并暴露一个Count的容器组件
export default connect(mapStateToProps,b)(CountUI)
