import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import store from './redux/store'



const root = ReactDOM.createRoot(document.getElementById('root'));
root.render( 
      
    <App />
      
);

//监测redux中状态的改变，如redux
store.subscribe(()=>{
    root.render(
        <App />
    )
})

