import React, { Component } from 'react'
import {connect} from 'react-redux'
import {createIncrementAction,createDecrementAction,createIncrementAsyncAction} from '../../redux/count_action_creator'

class Count extends Component {
  render() {
    return (
      <div>
        <h2>当前求和为：{this.props.count}</h2>
        <button onClick={this.props.jia}>+</button>
        <button onClick={this.props.jian()}>-</button>
        <button onClick={this.props.asyncJia()}>async+</button>
      </div>
    )
  }
}

export default connect(
  state => ({count: state.count}),
  {
    jia:createIncrementAction,
    jian:createDecrementAction,
    asyncJia:createIncrementAsyncAction
}
)(Count)
